import React, { useEffect, useRef, useState } from 'react'
import DefaultLayout from './layouts/DefaultLayout'
import './assets/styles/global.scss'
import { motion } from 'framer-motion'
import { useInView } from 'react-intersection-observer'

function App() {
  const [el, setEl] = useState('home')
  const { ref, inView } = useInView({
    threshold: 0
  })

  const { ref: ref1, inView: inView1 } = useInView({
    threshold: 0
  })

  const { ref: ref2, inView: inView2 } = useInView({
    threshold: 0
  })

  useEffect(() => {
    if (inView) {
      setEl('home')
    }
    if (inView1) {
      setEl('about')
    }
    if (inView2) {
      setEl('contact')
    }
  }, [inView, inView1, inView2])
  console.info('element:   ', el)
  return (
    <motion.div className='App'>
      <DefaultLayout>
        Home page
        <div
          ref={ref}
          id='home'
          style={{ width: '100vw', height: '100vh', background: 'green' }}
        >
          test
        </div>
        <div
          ref={ref1}
          id='about'
          style={{ width: '100vw', height: '100vh', background: 'yellow' }}
        ></div>
        <div
          ref={ref2}
          id='contact'
          style={{ width: '100vw', height: '100vh', background: 'red' }}
        ></div>
      </DefaultLayout>
    </motion.div>
  )
}

export default App
