import React, { useEffect, useRef, useState } from 'react'
import DefaultLayout from './layouts/DefaultLayout'
import './assets/styles/global.scss'
import { motion } from 'framer-motion'
import { useInView } from 'react-intersection-observer'
import { navbar } from './components/Shape/Navbar/common/data'

function App() {
  const [el, setEl] = useState('home')

  const { ref: headerRef, inView: headerInView } = useInView({
    threshold: 0
  })
  const { ref: aboutRef, inView: aboutInView } = useInView({
    threshold: 0
  })
  const { ref: skillRef, inView: skillInView } = useInView({
    threshold: 0
  })
  const { ref: experienceRef, inView: experienceInView } = useInView({
    threshold: 0
  })
  const { ref: projectsRef, inView: projectsInView } = useInView({
    threshold: 0
  })
  const { ref: contactRef, inView: contactInView } = useInView({
    threshold: 0
  })

  useEffect(() => {
    if (headerInView) {
      setEl('header')
    }
    if (aboutInView) {
      setEl('about')
    }
    if (skillInView) {
      setEl('skill')
    }
    if (experienceInView) {
      setEl('experience')
    }
    if (projectsInView) {
      setEl('projects')
    }
    if (contactInView) {
      setEl('contact')
    }
  }, [
    aboutInView,
    contactInView,
    experienceInView,
    headerInView,
    projectsInView,
    skillInView
  ])
  console.info(el)
  return (
    <motion.div className='App'>
      <DefaultLayout>
        <div
          ref={headerRef}
          // id='home'
          style={{ width: '100vw', height: '100vh', background: 'green' }}
        >
          test
        </div>
        <div
          ref={aboutRef}
          // id='home'
          style={{ width: '100vw', height: '100vh', background: 'green' }}
        >
          test
        </div>
        <div
          ref={skillRef}
          // id='home'
          style={{ width: '100vw', height: '100vh', background: 'green' }}
        >
          test
        </div>
        <div
          ref={experienceRef}
          // id='home'
          style={{ width: '100vw', height: '100vh', background: 'green' }}
        >
          test
        </div>
        <div
          ref={projectsRef}
          // id='home'
          style={{ width: '100vw', height: '100vh', background: 'green' }}
        >
          test
        </div>
        <div
          ref={contactRef}
          // id='home'
          style={{ width: '100vw', height: '100vh', background: 'green' }}
        >
          test
        </div>
      </DefaultLayout>
    </motion.div>
  )
}

export default App
